import React, { useState, useEffect, setMessage } from 'react';
import './App.css';

function App() {
  const [candidates, setCandidates] = useState({
    name: '',
    votes: 0,
  });
  const [votes, setVotes] = useState('')


  const handleChange = (e) => {
    const value = e.target.value; // Destructure name and value from event target
    setFormData(prevState => ({
      ...prevState,
      name: value, // Use the input's name to update the corresponding property
    }));
  };

  useEffect(() => {
    // Fetch the message from the Express server
    fetch('http://localhost:3001/get-candidates')
      .then(response => response.json())
      .then(candidates => setCandidates(candidates))
      .catch(error => console.error('Error fetching data:', error));
  }, []); // Empty dependency array means this effect runs once on mount

  const handleVote = async (name, e) => {
    e.preventDefault();
    console.log({ name: name })

    try {
      const response = await fetch('http://localhost:3001/vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: name }),
      });
      const voteResult = await response.json();
      console.log(voteResult)
      const updatedCandidates = candidates.map(candidate => {
        if (candidate.name === name) {
          return { ...candidate, votes: voteResult.vote };
        }
        return candidate;
      });
      setCandidates(updatedCandidates);
      // console.log(name)
      if (!response.ok) {
        console.log(response)
        throw new Error('Network response was not ok');
      }
      // Handle success response, maybe clear form or show a success message
    } catch (error) {
      console.error('Error:', error);
      // Handle errors, maybe show an error message to the user
    }


  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/add_candidate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },

        body: JSON.stringify(formData),
      });
      setCandidates(prevState => [...prevState, formData]);
      if (!response.ok) {
        console.log('response', response)
        throw new Error('Network response was not ok');
      }
      // Handle success response, maybe clear form or show a success message
    } catch (error) {
      console.error('Error:', error);
      // Handle errors, maybe show an error message to the user
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="Candidate"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <button style={{ backgroundColor: 'blue', color: 'white', padding: '10px 20px', margin: '10px', borderRadius: '5px', cursor: 'pointer' }} onClick={handleSubmit} type="submit">Submit</button>

      <ul>
        {candidates.map((candidate, index) => (
          <div>

     <li key={index}>{candidate.name} - Votes: {candidate.votes}</li>
          <button style={{ backgroundColor: 'blue', color: 'white', padding: '10px 20px', margin: '10px', borderRadius: '5px', cursor: 'pointer' }} onClick={(e) => handleVote(candidate.name, e)}>Update Vote</button>
            </div>
          ))}
      </ul>
      </form>
      </header>
    </div>
  );
}

export default App;
