import React, { useState, useEffect, setMessage } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    votes: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target.value;
    setFormData(prevState => ({
      ...prevState,
      [name]: name === 'votes' ? parseInt(value, 10) : value,
    }));
  };

  useEffect(() => {
    // Fetch the message from the Express server
    fetch('http://localhost:3001/add-candidate')
      .then(response => response.json())
      .then(data => setMessage(data.message))
      .catch(error => console.error('Error fetching data:', error));
  }, []); // Empty dependency array means this effect runs once on mount

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/add-candidate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Success:', data);
      // Handle success response, maybe clear form or show a success message
    } catch (error) {
      console.error('Error:', error);
      // Handle errors, maybe show an error message to the user
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="Candidate"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      </header>
    </div>
  );
}

export default App;
