import React, { useState, useEffect, setMessage } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    votes: 0,
  });
  const [candidates, setCandidates] = useState([]);
  const [vote, setVotes] = useState(0);

  const handleChange = (e) => {
    const  value  = e.target.value;
    setFormData(prevState => ({
      ...prevState,
    }));
    console.log(formData)
  };

  // useEffect(() => {
  //   // Fetch the message from the Express server
  //   fetch('http://localhost:3001/get-candidates')
  //     .then(response => response.json())
  //     .then(candidates => setCandidates(candidates))
  //     .catch(error => console.error('Error fetching data:', error));
  // }, []); // Empty dependency array means this effect runs once on mount

  const handleVote = (name) => {

    const updatedCandidates = candidates.map(candidate => {
      if (candidate === name) {
        candidate.votes += 1;
      }
      return candidate;
    });
    setCandidates(updatedCandidates);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/add_candidate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },

        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        console.log(response)
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Success:', data);
      // Handle success response, maybe clear form or show a success message
    } catch (error) {
      console.error('Error:', error);
      // Handle errors, maybe show an error message to the user
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="Candidate"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <button type="submit">Submit</button>
      <ul>
        {candidates.map((candidate, index) => (
          <div>
     <li key={index}>{candidate.name} - Votes: {candidate.votes}</li>
          <button  onClick={handleVote(candidate.name)}>Update Vote</button>
          </div>



          ))}
      </ul>
      </form>
      </header>
    </div>
  );
}

export default App;
